import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Signup.css'; // Ensure this path is correct

const Signup = ({ onSignup }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSignup = async () => {
    try {
      const response = await fetch('http://localhost:5208/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, passwordHash: password, role: 'user' }),
      });

      const responseBody = await response.json(); // Parse as JSON
      console.log('Response Body:', responseBody);

      if (response.ok) {
        console.log('Signup successful:', responseBody);
        onSignup(username);
      } else {
        setError('Signup failed');
        console.error('Signup failed:', response.status);
      }
    } catch (err) {
      console.error('Error during signup:', err);
      setError('An error occurred. Please try again.');
    }
  };

  return (
    <div className="signup">
      <h2>Sign Up</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleSignup}>Sign Up</button>
      
      {/* Sign In Link */}
      <div className="signin-link">
        <p>Already have an account?</p>
        <Link to="/login">
          <button className="signin-button">Sign In</button>
        </Link>
      </div>
    </div>
  );
};

export default Signup;
