import React, { useEffect, useState } from 'react';
import jwtDecode from 'jwt-decode';
import axios from 'axios';

const QuestionBlock = () => {
  const [bookmarks, setBookmarks] = useState([]);

  const loadBookmarks = async () => {
    try {
      const token = localStorage.getItem('token');

      if (!token || token.split('.').length !== 3) {
        console.error('Invalid token format. Please log in again.');
        localStorage.removeItem('token');
        // Optionally, redirect to login page
        return;
      }

      const decodedToken = jwtDecode(token); // Decoding the token to extract user ID or other info

      const response = await axios.get('http://localhost:5208/api/bookmarks', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setBookmarks(response.data);
    } catch (error) {
      console.error('Error fetching bookmarks:', error);
      // Handle error appropriately, e.g., show a message to the user
    }
  };

  useEffect(() => {
    loadBookmarks();
  }, []);

  return (
    <div>
      {/* Render your bookmarks here */}
    </div>
  );
};

export default QuestionBlock;
