import React, { useState, useEffect } from 'react';
import Tabs from './Tabs';
import CodeBlock from './CodeBlock';
import { addBookmark } from './bookmarkApi'; // Import your bookmark function
import { decode } from 'jwt-decode'; // Correct named import for jwt-decode
import '../styles/QuestionBlock.css';

const QuestionBlock = ({ question, code }) => {
  const [bookmarked, setBookmarked] = useState(false);

  useEffect(() => {
    // Optionally load bookmark status from server or localStorage
  }, []);

  const handleBookmark = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) throw new Error("User is not logged in");

      // Decode the JWT token to get the user ID
      const decodedToken = decode(token);
      const userId = decodedToken.userId; // Assuming the JWT contains `userId`

      // Create the bookmark request
      const bookmarkRequest = {
        userId: userId, // Extracted from the JWT
        questionId: question.id, // Assuming question object has an ID field
      };

      // Call the addBookmark function
      await addBookmark(bookmarkRequest);
      setBookmarked(true); // Update state on success
    } catch (error) {
      console.error('Error updating bookmark:', error);
    }
  };

  return (
    <div className="question-block">
      <h2>{question.title}</h2>
      <Tabs languages={['C', 'C++', 'Java', 'Python']} onTabClick={() => {}} />
      <CodeBlock code={code} />
      <button className="bookmark-button" onClick={handleBookmark}>
        {bookmarked ? 'Unbookmark' : 'Bookmark'}
      </button>
    </div>
  );
};

export default QuestionBlock;
