import axios from 'axios';

export const fetchBookmarks = async () => {
  const token = localStorage.getItem('token'); // Retrieve the token from localStorage

  try {
    const response = await axios.get('http://localhost:5208/api/bookmarks', {
      headers: {
        'Authorization': `Bearer ${token}` // Include the token in the Authorization header
      }
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching bookmarks:', error);
    throw error;
  }
};

export const addBookmark = async (bookmarkRequest) => {
  const token = localStorage.getItem('token');

  try {
    const response = await axios.post('http://localhost:5208/api/bookmarks/add', bookmarkRequest, {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      }
    });
    return response.data;
  } catch (error) {
    console.error('Error adding bookmark:', error);
    throw error;
  }
};

export const removeBookmark = async (bookmarkId) => {
  const token = localStorage.getItem('token');

  try {
    const response = await axios.delete(`http://localhost:5208/api/bookmarks/${bookmarkId}`, {
      headers: {
        'Authorization': `Bearer ${token}`,
      }
    });
    return response.data;
  } catch (error) {
    console.error('Error removing bookmark:', error);
    throw error;
  }
};
