import React, { useState } from 'react';
import '../styles/Login.css';

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async () => {
    try {
      const response = await fetch('http://localhost:5208/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const responseBody = await response.json(); // Parse as JSON
      console.log('Response Body:', responseBody);

      if (response.ok && responseBody.token) {
        console.log('Login successful:', responseBody);

        // Store token and username
        localStorage.setItem('token', responseBody.token);
        localStorage.setItem('loggedInUser', username);


        // Assuming role is included in the response
        const role = responseBody.role || 'user'; // Default to 'user' if role isn't provided
        localStorage.setItem('role', role);

        onLogin(username);
      } else {
        setError('Invalid credentials');
        console.error('Login failed:', response.status);
      }
    } catch (err) {
      console.error('Error during login:', err);
      setError('An error occurred. Please try again.');
    }
  };

  return (
    <div className="login">
      <h2>Login</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
};

export default Login;
