import axios from 'axios';

const API_URL = 'http://localhost:5208/api';

export const addBookmark = async (bookmarkData) => {
  try {
    const response = await axios.post(`${API_URL}/bookmarks/add`, bookmarkData, {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`,
      },
    });
    return response.data;
  } catch (error) {
    throw error;
  }
};

export const removeBookmark = async (questionId, userId) => {
  try {
    const response = await axios.delete(`${API_URL}/bookmarks/delete/${questionId}`, {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`,
      },
      data: { userId }
    });
    return response.data;
  } catch (error) {
    throw error;
  }
};

export const fetchBookmarks = async () => {
  try {
    const response = await axios.get(`${API_URL}/bookmarks`, {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`,
      },
    });
    return response.data;
  } catch (error) {
    throw error;
  }
};
