import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Tabs from './Tabs';
import CodeBlock from './CodeBlock';
import '../styles/QuestionBlock.css';

const QuestionBlock = ({ question, code }) => {
  const [bookmarked, setBookmarked] = useState(false);

  // Load token from localStorage
  const token = localStorage.getItem('token');

  // Load bookmarks on component mount
  useEffect(() => {
    const fetchBookmarks = async () => {
      try {
        const response = await axios.get('http://localhost:5208/api/bookmarks/user', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        const userBookmarks = response.data;
        setBookmarked(userBookmarks.includes(question.id));
      } catch (error) {
        console.error('Error fetching bookmarks:', error);
      }
    };

    if (token) {
      fetchBookmarks();
    }
  }, [question.id, token]);

  // Handle bookmark toggle
  const handleBookmark = async () => {
    try {
      const bookmarkRequest = { questionId: question.id };
      const url = bookmarked
        ? 'http://localhost:5208/api/bookmarks/remove'
        : 'http://localhost:5208/api/bookmarks/add';

      await axios.post(url, bookmarkRequest, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      setBookmarked(!bookmarked);
    } catch (error) {
      console.error('Error updating bookmark:', error);
    }
  };

  return (
    <div className="question-block">
      <h2>{question.title}</h2>
      <Tabs languages={['C', 'C++', 'Java', 'Python']} onTabClick={() => {}} />
      <CodeBlock code={code} />
      <button className="bookmark-button" onClick={handleBookmark}>
        {bookmarked ? 'Unbookmark' : 'Bookmark'}
      </button>
    </div>
  );
};

export default QuestionBlock;
