import React, { useState, useEffect } from 'react';
import bookmarkService from './bookmarkService';
import QuestionAndCodeBlock from './QuestionAndCodeBlock';
import '../styles/QuestionsList.css';  // Assuming this is the CSS used on the home page

const UserDashboard = () => {
  const [bookmarkedQuestions, setBookmarkedQuestions] = useState([]);

  useEffect(() => {
    const fetchBookmarkedQuestions = async () => {
      try {
        const bookmarks = await bookmarkService.getBookmarks();
        setBookmarkedQuestions(bookmarks);
      } catch (error) {
        console.error('Error fetching bookmarked questions:', error);
      }
    };

    fetchBookmarkedQuestions();
  }, []);

  return (
    <div className="questions-list-container">
      <h1>Your Bookmarked Questions</h1>
      {bookmarkedQuestions.length > 0 ? (
        bookmarkedQuestions.map((question) => (
          <div key={question.questionId} className="question-block">
            <h3 className="question-text">{question.questionText}</h3>
            <QuestionAndCodeBlock
              questionId={question.questionId}
              code={question.codeSnippet}
            />
          </div>
        ))
      ) : (
        <p>No bookmarked questions found.</p>
      )}
    </div>
  );
};

export default UserDashboard;
