import React, { useState, useEffect } from 'react';
import Tabs from './Tabs';
import CodeBlock from './CodeBlock';
import '../styles/QuestionBlock.css';

const QuestionBlock = ({ question, code }) => {
  const [bookmarked, setBookmarked] = useState(false);

  // Load bookmark status from localStorage on component mount
  useEffect(() => {
    const savedBookmarks = JSON.parse(localStorage.getItem('bookmarks')) || [];
    setBookmarked(savedBookmarks.includes(question));
  }, [question]);

  // Handle bookmark toggle
  const handleBookmark = () => {
    const savedBookmarks = JSON.parse(localStorage.getItem('bookmarks')) || [];
    let updatedBookmarks;

    if (bookmarked) {
      updatedBookmarks = savedBookmarks.filter((q) => q !== question);
    } else {
      updatedBookmarks = [...savedBookmarks, question];
    }

    localStorage.setItem('bookmarks', JSON.stringify(updatedBookmarks));
    setBookmarked(!bookmarked);
  };

  return (
    <div className="question-block">
      <h2>{question}</h2>
      <Tabs languages={['C', 'C++', 'Java', 'Python']} onTabClick={() => {}} />
      <CodeBlock code={code} />
      <button className="bookmark-button" onClick={handleBookmark}>
        {bookmarked ? 'Unbookmark' : 'Bookmark'}
      </button>
    </div>
  );
};

export default QuestionBlock;
