import React, { useRef } from 'react';
import QuestionBlock from './QuestionBlock';
import './QuestionsList.css';

const QuestionsList = ({ questionsData }) => {
  const questionRefs = useRef([]);

  return (
    <div className="questions-container">
      {questionsData.length === 0 ? (
        <p>No questions available or an issue occurred while fetching data.</p>
      ) : (
        questionsData.map((q, index) => (
          <div key={index} ref={(el) => (questionRefs.current[index] = el)} className="question-item">
            <h2 className="question-title">{q.questionText}</h2>
            <pre className="code-snippet">{q.codeSnippet}</pre>
            <QuestionBlock questionId={q.id} />
          </div>
        ))
      )}
    </div>
  );
};

export default QuestionsList;
